mber_1; //注意:旧版本PHP使用var 新版本PHP建议使用public; public $number_2; public function __construct($number_1 = null, $number_2 = null) { //构造函数 $this->number_1 = $number_1; $this->number_2 = $number_2; } function max() //求两个数的最大值 { return ($this->number_1 > $this->number_2) ? $this->number_1 : $this->number_2; } function swap() //交换两个数的值 { $temp = $this->number_1; $this->number_1 = $this->number_2; $this->number_2 = $temp; } } $max_and_swap_one = new max_and_swap(10, 20); // 实例化对象 $max_number = $max_and_swap_one->max(); // 调用对象方法max() echo \\\"最大值为:\\\" . $max_number; $max_and_swap_one->swap(); // 调用对象方法swap() echo \\\"number_1: \\\" . $max_and_swap_one->number_1; echo \\\"number_2: \\\" . $max_and_swap_one->number_2; //最大值为:20 //number_1: 20 //number_2: 10\",\"__LINE__ - 返回当前行号\",\"__FILE__ - 返回当前文件名\",\"__DIR__ - 返回当前文件所在目录\",\"__FUNCTION__ - 返回当前函数名\",\"__CLASS__ - 返回当前类名\",\"__METHOD__ - 返回当前方法名\",\"__NAMESPACE__ - 返回当前命名空间名\",\"_TRAIT__ - 返回当前Trait名\",\" class test_class { function test_function() { echo \\\"当前行号:\\\" . __LINE__; echo \\\"当前文件名:\\\" . __FILE__; echo \\\"当前文件所在目录:\\\" . __DIR__; echo \\\"当前函数名:\\\" . __FUNCTION__; echo \\\"当前类名:\\\" . __CLASS__; echo \\\"当前方法名:\\\" . __METHOD__; echo \\\"当前命名空间名:\\\" . __NAMESPACE__; echo \\\"当前Trait名:\\\" . __TRAIT__; } } $test_object = new test_class; $test_object->test_function(); /* 当前行号:1024 当前文件名:/www/wwwroot/youze.xn--6qq986b3xl/php_note.php 当前文件所在目录:/www/wwwroot/youze.xn--6qq986b3xl 当前函数名:test_function 当前类名:test_class 当前方法名:test_class::test_function 当前命名空间名: 当前Trait名:\",\"将 PHP 文件的内容插入另一个 PHP 文件\",\"require 会生成致命错误（E_COMPILE_ERROR）并停止脚本\",\"include 只生成警告（E_WARNING），并且脚本会继续\",\"省去重复工作,可以为所有页面创建标准页头、页脚或者菜单文件\",\" <?php // 页头文件 footer.php $color = '银色的'; $car = '奔驰轿车'; echo \\\"<p>Copyright 20020-\\\" . date(\\\"Y\\\") . \\\" Youze.comlt/p>\\\" ?> <?php // 主页文件 php_note.php include 'footer.php'; // 包含页脚文件 require引用方法相同 echo \\\"我有一辆\\\" . $color . $car .\\\".\\\"; ?> //Copyright 20020-2024 Youze.com //我有一辆银色的奔驰轿车.\",